import styled from "styled-components";
import { defaultTheme, typeScale } from "../utilities";
import { applyStyleModifiers } from "styled-components-modifiers";

const BUTTON_MODIFIERS = {
  small: ({props}) => `
    font-size: ${typeScale.helperText};
    padding: 8px;
  `,
  large: ({props}) => `
    font-size: ${typeScale.header5};
    padding: 16px 24px;
  `,
  warning: ({ props }) => `
    background: ${props.status.warningColor};
    color: ${props.textColorInverted};

    &:hover, &:focus {
        background-color: ${props.status.warningColorHover};
        outline: 3px solid ${props.status.warningColorHover};
        outline-offset: 2px;
        border: 2px solid transparent;
    }

    &:active {
        background-color: ${props.status.warningColorActive};
    }
  `,
  primaryButtonWarning: ({props}) => `
    background-color: ${props.status.warningColor};
    color: ${props.textColorInverted};
    `,
  secondaryButtonWarning: ({props}) => `
    background: none;
    border: 2px solid ${props.status.warningColor};
    color: ${props.status.warningColor};

    &:hover, &:focus {
        background-color: ${props.status.warningColorHover};
        outline: 3px solid ${props.status.warningColorHover};
        outline-offset: 2px;
        border: 2px solid transparent;
    }
  `,
  tertiaryButtonWarning: ({props}) => `
    background: none;
    color: ${props.status.warningColor};

    &:hover, &:focus {
        outline: 3px solid ${props.status.warningColorHover};
        outline-offset: 2px;
        border: 2px solid transparent;
    }
  `,
  error: ({props}) => `
    background: ${props.status.errorColor};
    color: ${props.textColorInverted};

    &:hover, &:focus {
        background-color: ${props.status.errorColorHover};
        outline: 3px solid ${props.status.errorColorHover};
        outline-offset: 2px;
        border: 2px solid transparent;
    }
    &:active {
        background-color: ${props.status.errorColorActive};
    }
  `,
  primaryButtonError: ({props}) => `
    background-color: ${props.status.errorColor};
    color: ${props.textColorInverted};
  `,
  secondaryButtonError: ({props}) => `
    background: none;
    border: 2px solid ${props.status.errorColor};
    color: ${props.status.errorColor};

    &:hover, &:focus {
        outline: 3px solid ${props.status.errorColorHover};
        outline-offset: 2px;
        border: 2px solid transparent;
    }
  `,
  tertiaryButtonError: ({props}) => `
    background: none;
    border: none;
    color: ${props.status.errorColor};

    &:hover, &:focus {
        outline: 3px solid ${props.status.errorColorHover};
        outline-offset: 2px;
        border: 2px solid transparent;
    }
  `,
  success: ({props}) => `
    background: none;
    color: ${props.status.successColor};

    &:hover, &:focus {
        background-color: ${props.status.successColorHover};
        outline: 3px solid ${props.status.successColorHover};
        outline-offset: 2px;
        border: 2px solid transparent;
    }

    &:active {
        background-color: ${props.status.successColorActive};
    }
  `,
  primaryButtonSuccess: ({props}) => `
    background-color: ${props.status.successColor};
    color: ${props.textColorInverted};
    `,
  secondaryButtonSuccess: ({props}) => `
    border: 2px solid ${props.status.successColor};
  `,
};

const Button = styled.button`
  padding: 12px 24px;
  font-size: ${typeScale.paragraph};
  border-radius: 5px;
  min-width: 100px;
  cursor: pointer;
  font-family: "Roboto Slab", monospace;
  transition: background-color 0.2s linear, color 0.2s linear;

  &:hover {
    background-color: ${props.primaryHoverColor};
    color: ${props.textColorOnPrimary};
  }

  &:focus {
    outline: 3px solid ${props.primaryHoverColor};
    outline-offset: 2px;
  }

  &:active {
    background-color: ${props.primaryActiveColor};
    border-color: ${props.primaryActiveColor};
    color: ${props.textColorOnPrimary};
  }
`;

const PrimaryButton = styled(Button)`
  background-color: ${props.primaryColor};
  border: none;
  color: white;

  &:disabled {
    background-color: ${props.disabled};
    color: ${props.textOnDisabled};
    cursor: not-allowed;
  }

  ${applyStyleModifiers(BUTTON_MODIFIERS)};
`;

export const SecondaryButton = styled(Button)`
  background: none;
  border: 2px solid ${props.primaryColor};
  color: ${props.primaryColor};

  &:disabled {
    background: none;
    color: ${props.disabled};
    border: 2px solid ${props.disabled};
    cursor: not-allowed;
  }

  ${applyStyleModifiers(BUTTON_MODIFIERS)};
`;
export const TertiaryButton = styled(Button)`
  background-color: none;
  border: none;
  color: ${props.primaryColor};

  &:disabled {
    background: none;
    color: ${props.disabled};
    cursor: not-allowed;
  }

  ${applyStyleModifiers(BUTTON_MODIFIERS)};
`;

export default PrimaryButton;
